// src/shipping.js
import React, { useState } from 'react';

const departamentos = ["Lima", "Arequipa", "Trujillo", "Cajamarca", "Otros"]; // Agrega más departamentos según se necesite
const provincias = {
  Lima: ["Lima Metropolitana", "Cañete", "Barranca"], // Ejemplo, agregar otras provincias
  Arequipa: ["Arequipa", "Camaná", "Caravelí"],
  Trujillo: ["Trujillo", "Ascope", "Pacasmayo"],
  Cajamarca: ["Cajamarca", "Jaén", "Chota"],
  Otros: ["Provincia Genérica"] // Puedes personalizar o agregar otras provincias y departamentos
};
const distritos = {
  "Lima Metropolitana": ["Miraflores", "San Isidro", "Surco"],
  "Arequipa": ["Cercado", "Cayma", "Yanahuara"],
  "La libertad": ["Centro", "Víctor Larco", "El Porvenir"],
  "Cajamarca": ["Centro", "Baños del Inca", "San Miguel"],
  "Provincia Genérica": ["Distrito Genérico"] // Opcional, para "Otros"
};

const Shipping = () => {
  const [selectedDepartamento, setSelectedDepartamento] = useState("");
  const [selectedProvincia, setSelectedProvincia] = useState("");
  const [selectedDistrito, setSelectedDistrito] = useState("");
  const [shippingCost, setShippingCost] = useState(null);

  const handleDepartamentoChange = (event) => {
    setSelectedDepartamento(event.target.value);
    setSelectedProvincia("");
    setSelectedDistrito("");
  };

  const handleProvinciaChange = (event) => {
    setSelectedProvincia(event.target.value);
    setSelectedDistrito("");
  };

  const calculateShippingCost = () => {
    let baseCost = 10; // Envío base a Lima, Arequipa, Trujillo, Cajamarca
    let extraCost = selectedDepartamento === "Otros" ? 1 : 0;

    if (selectedDepartamento !== "Otros") {
      extraCost = distritos[selectedProvincia]?.indexOf(selectedDistrito) + 1 || 1;
    }

    setShippingCost(baseCost + extraCost);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "20px" }}>
      <h2>Shipping Page</h2>
      <form>
        <label>Departamento:</label>
        <select
          value={selectedDepartamento}
          onChange={handleDepartamentoChange}
        >
          <option value="">Seleccionar Departamento</option>
          {departamentos.map((dep) => (
            <option key={dep} value={dep}>{dep}</option>
          ))}
        </select>

        {selectedDepartamento && (
          <>
            <label>Provincia:</label>
            <select
              value={selectedProvincia}
              onChange={handleProvinciaChange}
            >
              <option value="">Seleccionar Provincia</option>
              {provincias[selectedDepartamento].map((prov) => (
                <option key={prov} value={prov}>{prov}</option>
              ))}
            </select>
          </>
        )}

        {selectedProvincia && (
          <>
            <label>Distrito:</label>
            <select
              value={selectedDistrito}
              onChange={(e) => setSelectedDistrito(e.target.value)}
            >
              <option value="">Seleccionar Distrito</option>
              {distritos[selectedProvincia].map((dist) => (
                <option key={dist} value={dist}>{dist}</option>
              ))}
            </select>
          </>
        )}

        <button
          type="button"
          onClick={calculateShippingCost}
          style={{ display: "block", margin: "10px auto" }}
        >
          Calcular Envío
        </button>
      </form>
      {shippingCost !== null && (
        <p>El costo de envío es: S/{shippingCost} soles</p>
      )}
    </div>
  );
};

export default Shipping;
