import { useParams } from "react-router-dom";
import "./tipoFerreteria";

const ProductDetail = () => {
  const { id } = useParams();
  // Busca el producto por ID, dependiendo de dónde tengas almacenada la lista de productos.
  const product = TornillosList.find((item) => item.id === parseInt(id));

  if (!product) {
    return <p>Producto no encontrado</p>;
  }

  return (
    <div className="producto-detalle">
      <img src={product.image} alt={product.title} className="Imagen-detalle" />
      <h2>{product.title}</h2>
      <p>{product.description}</p>
      <p>{product.products} productos</p>
      <button className="add-to-cart">Agregar al carrito</button>
    </div>
  );
};

export default ProductDetail;
