// FerreteriaDetalle.js
import React from "react";
import { useParams } from "react-router-dom";
import "./tipoFerreteria"; 
import "./Ferreteria.css";

const FerreteriaDetalle = () => {
  const { id } = useParams();

  // Encuentra el producto correspondiente
  const product = TornillosList.find((item) => item.id === parseInt(id));

  if (!product) {
    return <p>Producto no encontrado</p>;
  }

  return (
    <div className="producto-detalle">
      <img src={product.image} alt={product.title} className="Imagen-detalle" />
      <h2>{product.title}</h2>
      <p>{product.description}</p>
      <p>{product.products} productos</p>
    </div>
  );
};

export default FerreteriaDetalle;
